import { createGlobalStyle } from 'styled-components'

export const GlobalStyles = createGlobalStle `
    html, body {
        box-sizing: border-box;
        margin: 0;
        padding: 0;
        position: relative;
        top: 300px;
        background: #ff9c00;
    }
`
